def clean_data(data):
    stemmer = PorterStemmer()
    cleaned_data = dict()
    stop_words = stopwords.words("english")

    for index, message in enumerate(data):
        message = BeautifulSoup(message, "html.parser").get_text()
        for word in set(word_tokenize(message.lower())):
            if word not in stop_words and word.isalpha():
                stemmed_word = stemmer.stem(word)
                if cleaned_data.get(index):
                    cleaned_data[index].append(stemmed_word)
                else:
                    cleaned_data[index] = [stemmed_word]
    return cleaned_data


